sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
     var convertfrom = api.state.convertfrom;
    api.setState('convertfrom',api.state.convertto);
    api.setState('convertto',convertfrom);
    api.setState('convertoexc',api.state.convertto);
    api.setState('convertfromexc',convertfrom);
    console.log(convertfrom);
}
